(function($){$.fn.pixelRain=function(options){var pixels=[],bodyWidth=this.width()-30,bodyHeight=this.height()-30;var settings=$.extend({amount:100,interval:100,pixelColor:"#000000"},options);var elContainer=document.createElement('div'),container=$(elContainer);container.css('width',bodyWidth+'px').css('height',bodyHeight+'px').css('position','absolute').css('top','15px').css('left','15px').css('z-index','-1000');this.append(container);while(settings.amount--){var randL=Math.floor((Math.random()*bodyWidth)+1),randT=Math.floor((Math.random()*bodyHeight)+1),elPixel=document.createElement('div'),pixel=$(elPixel);pixel.css('width','2px').css('height','2px').css('background-color',settings.pixelColor).css('position','absolute').css('left',randL).css('top',randT);pixels.push(pixel);container.append(pixel)}var movePixels=function(){for(var i=0;i<pixels.length;i++){var randL=Math.floor((Math.random()*bodyWidth)+1),randT=Math.floor((Math.random()*bodyHeight)+1);$(pixels[i]).css('left',randL).css('top',randT)}};setInterval(movePixels,settings.interval)}}(jQuery));
